PROCEDURE PDATA (		PRM_MODE	VARCHAR2 DEFAULT NULL,
				PRM_CHECK	VARCHAR2 DEFAULT NULL,
				PRM_DATA	VARCHAR2 DEFAULT NULL,
				PRM_OTHER	VARCHAR2 DEFAULT NULL )  AS

BEGIN
    IF  PRM_MODE = PRM_CHECK THEN
	    HTP.P(PRM_DATA);
