		END CASE;
	ELSE
	    HTP.P('#alert '||FUN.LANG('Micro coluna n&atilde;o encontrada com nome e a view definidos!')||'');
	END IF;
EXCEPTION 
	WHEN WS_NOUSER THEN
		HTP.P('Sem permiss&atilde;o!');
