	    EXIT WHEN NC_COLUNAS%NOTFOUND;
	END LOOP;
	CLOSE NC_COLUNAS;

	WS_TMP := '<script>';
	WS_TMP := WS_TMP||'var incrx=parent.document.getElementById(''lagrupadores''); ';
