	WS_ROTULO			DBMS_SQL.VARCHAR2_TABLE;
	WS_TIPO				DBMS_SQL.VARCHAR2_TABLE;
	WS_SUFIXO			DBMS_SQL.VARCHAR2_TABLE;
	WS_SCRIPT			DBMS_SQL.VARCHAR2_TABLE;
	WS_LIST				DBMS_SQL.VARCHAR2_TABLE;
