	CURSOR CRS_OBJ IS
		SELECT CD_OBJETO, COD, VISAO, NM_OBJETO, CD_GRUPO, NM_GRUPO, TP_OBJETO
		FROM ( 
			SELECT CD_OBJETO, COD, NVL(T3.CD_MICRO_VISAO, 'N/A') AS VISAO, 
			NM_OBJETO, T1.CD_GRUPO, NM_GRUPO,T1.TP_OBJETO
