					EXIT WHEN CRS_FILTROB%NOTFOUND;

					WS_COUNTER := WS_COUNTER+1;

					HTP.P('<tr>');
                        IF WS_FILTROB.CD_CRIADO = WS_USUARIO OR WS_ADMIN = 'A' THEN

						    IF WS_ADMIN = 'A' THEN
							
