									FCL.BUTTON_LIXO('dl_ofiltro','prm_key', WS_FILTRO.CD_FILTRO);
								HTP.P('</td>');
					        ELSE
                                HTP.P('<td><a title="'||FUN.LANG('Sem premisso')||'" class="noremove">X</a></td>');
							END IF;

					HTP.P('</tr>');
				END LOOP;
				CLOSE CRS_FILTRO;
            END IF;
        HTP.P('</tbody>');
	HTP.TABLECLOSE;
EXCEPTION
	WHEN WS_NOUSER THEN
		HTP.P('Sem permiss&atilde;o!');
	WHEN OTHERS THEN
		HTP.P(SQLERRM);
