		SELECT T1.CD_OBJETO, COD, NM_OBJETO NOME, TP_OBJETO TIPO, CD_OBJETO_GO, CD_MICRO_VISAO VISAO, ORDEM
		FROM GOTO_OBJETO T1
		LEFT JOIN 
		PONTO_AVALIACAO ON CD_PONTO = T1.CD_OBJETO_GO
		LEFT JOIN
		OBJETOS T3 ON T3.CD_OBJETO  = T1.CD_OBJETO_GO
