		CASE WHEN PRM_DIR = '1' THEN DECODE(PRM_ORDER, '1', T1.CD_OBJETO, '2', T1.CD_OBJETO_GO, '3', VISAO, '4', TIPO, T1.CD_OBJETO) END ASC,
