				HTP.P('</li>');

			HTP.P('<li>');
			    HTP.P('<span class="before">'||FUN.LANG('Descritivo')||'</span>');
				HTP.P('<span class="after">');
				    HTP.P('<textarea onblur="update_prop('''||PRM_OBJECT||''', ''descritivo'', this.value);">'||WS_OBJ.DS_OBJETO||'</textarea>');
			    HTP.P('</span>');
			HTP.P('</li>');

		HTP.FORMCLOSE;
		HTP.P('</ul>');

	HTP.P('</div>');
	
	FCL.CLOSER_MENU;
EXCEPTION
	WHEN WS_NOUSER THEN
		HTP.P('Sem permiss&atilde;o!');
