			WS_USUARIO := USER;
		    WS_ADMIN   := 'N';

		ELSE
	        WS_USUARIO := GBL.GETUSUARIO;
		    WS_ADMIN   := GBL.GETNIVEL;

		END IF;

		IF NVL(WS_USUARIO, 'NOUSER') = 'NOUSER' THEN
			RAISE WS_NOUSER;
		END IF;

