    WS_USUARIO := GBL.GETUSUARIO;

	IF WS_USUARIO = 'NOUSER' THEN
		RAISE WS_NOUSER;
	END IF;

    WS_COUNT := 0;
	IF  PRM_SCREEN = 'SHOWSCREEN' THEN
	    OPEN CRS_OBJLOC;
	        LOOP
		        FETCH CRS_OBJLOC INTO WS_OBJLOC;
		        EXIT WHEN CRS_OBJLOC%NOTFOUND;
		    END LOOP;
	    CLOSE CRS_OBJLOC;
