        IF INSTR(PRM_OBJETO, 'SECTION') = 0 THEN
		    IF INSTR(PRM_OBJETO, 'ARTICLE') = 0 THEN
                
                SELECT COUNT(*) INTO WS_COUNT
                FROM OBJECT_LOCATION 
                WHERE OBJECT_ID = PRM_OBJETO AND 
