PROCEDURE DL_DRE ( PRM_MAPA VARCHAR2 DEFAULT NULL ) AS

    WS_COUNT NUMBER;

	BEGIN
		
		SELECT COUNT(*) INTO WS_COUNT FROM DEFF_PADRAO WHERE CD_MAPA = UPPER(TRIM(PRM_MAPA));

		IF WS_COUNT > 0 THEN
	        HTP.P('#alert '||FUN.LANG('DRE possui niveis, imposs&iacute;vel excluir')||'!');
