		WHEN NOT MATCHED THEN
			INSERT VALUES (WS_USUARIO||REPLACE(SYS_CONTEXT('userenv','ip_address'), '.'), WS_USUARIO, SYSDATE, SYSDATE, 'A'); 
		*/ 	
		COMMIT;
