		GBL.SETUSUARIO(TRIM(UPPER(WS_USUARIO)), '');
        
	WHEN WS_LOGOUT THEN
		
		HTP.P('0');
		INSERT INTO BI_LOG_SISTEMA VALUES(SYSDATE, 'FIM DA SESS&Atilde;O', USER, 'EVENTO');
		COMMIT;
	WHEN OTHERS THEN
		HTP.P('0');
		

