 ) AS

	WS_CODIGO          VARCHAR2(40);
	WS_PARAMETRO       VARCHAR2(2000);
	WS_VALOR           VARCHAR2(2000);
	WS_NOME            VARCHAR2(80);
	WS_FILTRO_COLUNA   VARCHAR2(80);
	WS_FILTRO_CONTEUDO VARCHAR2(80);
	WS_USUARIO         VARCHAR2(80);
	WS_GRUPO		   VARCHAR2(80);
