 WS_OK        EXCEPTION;
 WS_USUARIO   VARCHAR2(80);

BEGIN

	SELECT COUNT(*) INTO WS_VERIFICA
    FROM ALL_JOBS
    WHERE UPPER(WHAT) LIKE 'SCH.MAIN;';

	

